# Examples:
#
#   cities = City.create([{ name: 'Chicago' }, { name: 'Copenhagen' }])
#   Mayor.create(name: 'Emanuel', city: cities.first)

users = []

demo = User.create({email: 'guest', password: 'password', first_name: 'Guest', last_name: 'User'})
demo2 = User.create({email: 'jeff', password: 'password', first_name: 'Jeffrey', last_name: 'Fan'})
demo3 = User.create({email: 'jeffrey', password: 'password', first_name: Faker::Name.first_name, last_name: Faker::Name.last_name})
demo4 = User.create({email: 'jeff123', password: 'password', first_name: Faker::Name.first_name, last_name: Faker::Name.last_name})
demo5 = User.create({email: 'jeffrey123', password: 'password', first_name: Faker::Name.first_name, last_name: Faker::Name.last_name})
users.push(demo)
users.push(demo2)
users.push(demo3)
users.push(demo4)
users.push(demo5)

50.times do
  email = Faker::Internet.unique.email
  password = 'password'
  first_name = Faker::Name.first_name
  last_name = Faker::Name.last_name
  User.create({email: email, password: password, first_name: first_name, last_name: last_name})
  users.push(User.last)
end


